package br.ufba.mata62.eleicoestransparentes.model;

import com.j256.ormlite.field.DatabaseField;
import com.j256.ormlite.table.DatabaseTable;

@DatabaseTable(tableName = "AgenteEleitoral")
public class AgenteEleitoral {
	
	// id is generated by the database and set on the object automagically
	@DatabaseField(generatedId = true)
	private int id;
	
	@DatabaseField(foreign = true)
	private Pessoa pessoa;
	
	@DatabaseField(foreign = true)
	private Comite comite;

	public int getId() {
		return id;
	}

	public void setId(int id) {
		this.id = id;
	}

	public Pessoa getPessoa() {
		return pessoa;
	}

	public void setPessoa(Pessoa pessoa) {
		this.pessoa = pessoa;
	}

	public Comite getComite() {
		return comite;
	}

	public void setComite(Comite comite) {
		this.comite = comite;
	}
	
	public Class getTipoAgente(){
		if(pessoa != null) return Pessoa.class;
		if(comite != null) return Comite.class;
		return null;
	}
	
	

}
